mapper.select:
select *
from tab_syslog



            visitTime >= #{parameterMap.startTime}


            and visitTime <= #{parameterMap.endTime}


            and ip like concat('%',#{parameterMap.ip},'%')


            and username like concat('%',#{parameterMap.username},'%')


            and url like concat('%',#{parameterMap.url},'%')



limit #{pageStart},#{pageSize}

select count(*) from tab_syslog
visitTime >= #{parameterMap.startTime}
and visitTime <= #{parameterMap.endTime}
and ip like concat('%',#{parameterMap.ip},'%')
and username like concat('%',#{parameterMap.username},'%')
and url like concat('%',#{parameterMap.url},'%')
